function selectPhoto(e){var t=document.getElementById("photo"+e);t.classList.contains("selected")?(t.classList.remove("selected"),t.style.border="none"):(t.classList.add("selected"),t.style.border="3px solid red"),updateSelectedPhotos()}function updateSelectedPhotos(){var e=Array.from(document.querySelectorAll(".selected")).map(e=>e.id.replace("photo",""));document.getElementById("selectedPhotos").value=e.join(", ")}function submitForm(e){var t;e.preventDefault(),sendOrderInfo(document.getElementById("selectedPhotos").value,document.getElementById("addressPhone").value)}function sendOrderInfo(e,t){event.preventDefault(),window.location.href="mailto:etumanov77@gmail.com?subject="+encodeURIComponent("Photo Order")+"&body="+encodeURIComponent("Selected photo numbers for the order: "+e+"\nAddress and phone: "+t)}let currentSlide=1,slideInterval;function startSlideShow(){slideInterval=setInterval(function(){changeSlide(1)},1500)}function stopSlideShow(){clearInterval(slideInterval)}function changeSlide(e){showSlide(currentSlide+=e)}function showSlide(e){let t=document.getElementsByClassName("carousel-image");e>t.length&&(currentSlide=1),e<1&&(currentSlide=t.length);let n=-100*(currentSlide-1)+"%";document.getElementById("image-container").style.transform="translateX("+n+")"}document.addEventListener("DOMContentLoaded",function(){startSlideShow()}),document.getElementById("next-button").addEventListener("click",function(){stopSlideShow(),changeSlide(1),startSlideShow()}),document.getElementById("prev-button").addEventListener("click",function(){stopSlideShow(),changeSlide(-1),startSlideShow()});
